import './index.css';
import { useState, useEffect } from "react";
import React from "react";

function Main(){
    const space = "%20"
    const riotKey = 'RGAPI-0087df1a-e82b-4267-92e5-20d7dabf042f'
    const summonerByName = 'https://na1.api.riotgames.com/tft/summoner/v1/summoners/by-name/'
    var response = null

    const [query, setQuery] = useState('')
    const [link, setLink] = useState('')
    const [user, updateUser] = useState({
        accountId: '',
        id: '',
        name: '',
        profileIconId: 0,
        puuid: '',
        revisionDate: 0,
        summonerLevel: 0,
    })

    async function submit(){
        console.log(link)
        response = await fetch(link)
        response = await response.json()
        updateUser({
            accountId: response.accountId,
            id: response.id,
            name: response.name,
            profileIconId: response.profileIconId,
            puuid: response.puuid,
            revisionDate: response.revisionDate,
            summonerLevel: response.summonerLevel,
        })

        // console.log(user)

    }

    useEffect(() => {
        const newQuery = query.replace(/ /, space)
        setLink(`${summonerByName}${newQuery}?api_key=${riotKey}`)

        if(response != null){
            console.log('yes')
            updateUser({
                accountId: response.accountId,
                id: response.id,
                name: response.name,
                profileIconId: response.profileIconId,
                puuid: response.puuid,
                revisionDate: response.revisionDate,
                summonerLevel: response.summonerLevel,
            })
        }

    })

    return(
        <main>
            <div>
            <input 
                type="text" 
                placeholder="Search..." 
                onChange={e => {
                    setQuery(e.target.value)

                }}
            />



            </div>
            <button
                onClick={() => submit() }
            >
                Submit
            </button>
            <h4>
                {link}
            </h4>
            <h4> {user.accountId} </h4>
            <h4> {user.id} </h4>
            <h4> {user.name} </h4>
            <h4> {user.profileIconId} </h4>
            <h4> {user.puuid} </h4>
            <h4> {user.revisionDate} </h4>
            <h4> {user.summonerLevel} </h4>

        </main>
    )
}

export default Main;
