import './index.css';
import { useState, useEffect } from "react";
import React from "react";

function Main(){
    const space = "%20"
    const riotKey = 'RGAPI-0087df1a-e82b-4267-92e5-20d7dabf042f'
    const summonerByName = 'https://na1.api.riotgames.com/tft/summoner/v1/summoners/by-name/'
    const [query, setQuery] = useState('')
    const [link, setLink] = useState('')
    const [user, updateUser] = useState({
        accountId: '',
        id: '',
        name: '',
        profileIconId: 0,
        puuid: '',
        revisionDate: 0,
        summonerLevel: 0,
    })

    async function submit(){
        console.log(link)
        let response = await fetch(link)
        response = await response.json()
        console.log(response)

        updateUser({
            accountId: response.accountId
        })
        console.log(user)

    }

    useEffect(() => {
        const newQuery = query.replace(/ /, space)
        setLink(`${summonerByName}${newQuery}?api_key=${riotKey}`)
    })

    return(
        <main>
            <div>
            <input 
                type="text" 
                placeholder="Search..." 
                onChange={e => {
                    setQuery(e.target.value)

                }}
            />



            </div>
            <button
                onClick={() => submit() }
            >
                Submit
            </button>
            <h1>
                {link}
            </h1>
        </main>
    )
}

export default Main;
